pragma solidity ^0.5.0;

// This contract is triggered by an inbound oracle when the soil moisture level falls below a certain threshold
event SoilMoistureLow(uint plantationId);

// This contract is triggered by an oracle network when the market price of coconuts reaches a certain level
event CoconutPriceHigh(uint plantationId, uint price);

// This contract stores information about the plantation, including the current soil moisture level and the number of coconuts available for sale
contract CoconutPlantation {
    uint public soilMoisture;
    uint public coconutsForSale;

    function updateSoilMoisture(uint newSoilMoisture) public {
        soilMoisture = newSoilMoisture;

        // If the soil moisture level falls below a certain threshold, trigger the SoilMoistureLow event
        if (soilMoisture < 50) {
            emit SoilMoistureLow(msg.sender);
        }
    }

    function updateCoconutsForSale(uint newCoconutsForSale) public {
        coconutsForSale = newCoconutsForSale;
    }

    function sellCoconuts(uint amount) public {
        require(coconutsForSale >= amount, "Not enough coconuts available for sale");
        coconutsForSale -= amount;
    }
}

// This contract is triggered by the SoilMoistureLow event and automatically adjusts the irrigation system
contract IrrigationController {
    CoconutPlantation public plantation;

    constructor(CoconutPlantation _plantation) public {
        plantation = _plantation;
    }

    function receiveSoilMoistureLow(uint plantationId) public {
        require(plantationId == plantation.id, "Incorrect plantation ID");

        // Increase the irrigation for the plantation
        // (implementation details left out for simplicity)
        adjustIrrigation(plantation, 50);
    }
}

// This contract is triggered by the CoconutPriceHigh event and automatically sells a certain amount of coconuts
contract CoconutSeller {
    CoconutPlantation public plantation;

    constructor(CoconutPlantation _plantation) public {
        plantation = _plantation;
    }

    function receiveCoconutPriceHigh(uint plantationId, uint price) public {
        require(plantationId == plantation.id, "Incorrect plantation ID");

        // Sell 100 coconuts at the current market price
        plantation.sellCoconuts(100);
    }
}

